import { useRef } from "react";
import { createUserWithEmailAndPasswordAndUser } from "firebase/auth";  
import { auth } from "../firebase/firebase.js";                 

const Register = () => {
  const usernameRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();

  const handleSubmit = async (event) => {
    event.preventDefault();
    const username = usernameRef.current.value;
    const email = emailRef.current.value;
    const password = passwordRef.current.value;

    try {
      const user = await createUserWithEmailAndPassword(auth, email, password);
      console.log("Registered:", username, email, password);
      // เพิ่มการจัดการอื่น ๆ ตามต้องการ
    } catch (error) {
      console.error("Registration error:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="form-container">
      <h2>Register</h2>
      <div>
        <label htmlFor="username">Username:</label>
        <input type="text" id="username" name="username" required ref={usernameRef} />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input type="email" id="email" name="email" required ref={emailRef} />
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input type="password" id="password" name="password" required ref={passwordRef} />
      </div>
      <button type="submit">Save</button>
    </form>
  );
};

export default Register;
