import { 
  fetchSignInMethodsForEmail, 
  GoogleAuthProvider, 
  FacebookAuthProvider, 
  signInWithPopup, 
  signInWithEmailAndPassword, 
  sendPasswordResetEmail 
} from "firebase/auth";
import { useRef, useState } from "react";
import { auth, db } from "../firebase/firebase.js";
import { doc, setDoc, getDoc } from "firebase/firestore";

const googleProvider = new GoogleAuthProvider();
const facebookProvider = new FacebookAuthProvider();

const Login = () => {
  const emailRef = useRef();
  const passwordRef = useRef();
  const [errorMessage, setErrorMessage] = useState("");
  const [wrongPasswordCount, setWrongPasswordCount] = useState(0);
  const [lastTriedEmail, setLastTriedEmail] = useState("");

  const handleEmailLogin = async (event) => {
    event.preventDefault();
    setErrorMessage("");

    let email = emailRef.current.value.trim().toLowerCase();
    const password = passwordRef.current.value;

    // รีเซ็ต counter ถ้าเปลี่ยน email
    if (email !== lastTriedEmail) {
      setWrongPasswordCount(0);
      setLastTriedEmail(email);
    }

    // ตรวจสอบรูปแบบ email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setErrorMessage("ชื่อผู้ใช้งานไม่ถูกต้อง");
      return;
    }

    try {
      const signInMethods = await fetchSignInMethodsForEmail(auth, email);

      // ไม่มี email ในระบบ
      if (signInMethods.length === 0) {
        setErrorMessage("ชื่อผู้ใช้งานไม่ถูกต้อง");
        return;
      }

      // ถ้าบัญชีนี้สมัครด้วยวิธีอื่นที่ไม่ใช่ password
      if (!signInMethods.includes("password")) {
        setErrorMessage(`บัญชีนี้สมัครด้วย ${signInMethods.join(", ")} โปรดเข้าสู่ระบบด้วยวิธีนั้น`);
        return;
      }

      // ล็อกอิน
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      console.log("Email login successful:", userCredential.user.uid);
      setWrongPasswordCount(0);
    } catch (error) {
      console.error("Email login error:", error);

      if (error.code === "auth/wrong-password" || error.code === "auth/invalid-credential") {
        const newCount = wrongPasswordCount + 1;
        setWrongPasswordCount(newCount);
        if (newCount >= 3) {
          setErrorMessage("ลืมรหัสผ่านหรือไม่");
        } else {
          setErrorMessage("รหัสผ่านไม่ถูกต้อง");
        }
      } else if (error.code === "auth/too-many-requests") {
        setErrorMessage("ลองผิดบ่อยเกินไป กรุณารอสักครู่แล้วลองใหม่");
      } else {
        setErrorMessage("เกิดข้อผิดพลาดในการเข้าสู่ระบบ");
      }
    }
  };

  const handleGoogleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const user = result.user;
      const userDocRef = doc(db, "users", user.uid);
      const userDoc = await getDoc(userDocRef);

      if (!userDoc.exists()) {
        await setDoc(userDocRef, {
          username: user.displayName || "No Name",
          email: user.email,
          telephone: "",
        });
      }
      console.log("Google login successful:", user.uid);
    } catch (error) {
      console.error("Google login error:", error);
    }
  };

  const handleFacebookLogin = async () => {
    try {
      const result = await signInWithPopup(auth, facebookProvider);
      const user = result.user;
      const userDocRef = doc(db, "users", user.uid);
      const userDoc = await getDoc(userDocRef);

      if (!userDoc.exists()) {
        await setDoc(userDocRef, {
          username: user.displayName || "No Name",
          email: user.email,
          telephone: "",
        });
      }
      console.log("Facebook login successful:", user.uid);
    } catch (error) {
      console.error("Facebook login error:", error);
    }
  };

  const handleForgotPassword = async () => {
    const email = emailRef.current.value.trim().toLowerCase();
    if (!email) {
      alert("กรุณากรอกอีเมลก่อน");
      return;
    }
    try {
      await sendPasswordResetEmail(auth, email);
      alert("ส่งลิงก์รีเซ็ตรหัสผ่านไปที่อีเมลแล้ว");
    } catch (err) {
      alert("ไม่สามารถส่งรีเซ็ตรหัสผ่าน: " + err.message);
    }
  };

  return (
    <>
      <h2>เข้าสู่ระบบ</h2>
      <form onSubmit={handleEmailLogin}>
        <div>
          <label>Email:</label>
          <input type="email" ref={emailRef} required />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" ref={passwordRef} required />
        </div>

        {errorMessage && (
          <p style={{ color: "red" }}>
            {errorMessage}{" "}
            {errorMessage.includes("ลืมรหัสผ่าน") && (
              <button type="button" onClick={handleForgotPassword}>
                คลิกที่นี่
              </button>
            )}
          </p>
        )}

        <button type="submit">Login</button>
      </form>

      <p>
        ยังไม่มีบัญชีผู้ใช้ ? <a href="/register">Sign Up</a>
      </p>

      <h2>or connect with</h2>
      <button onClick={handleGoogleLogin}>Login with Google</button>
      <button onClick={handleFacebookLogin}>Login with Facebook</button>
    </>
  );
};

export default Login;
