import { signInWithEmailAndPassword, fetchSignInMethodsForEmail, GoogleAuthProvider, signInWithPopup, FacebookAuthProvider, sendPasswordResetEmail } from "firebase/auth";
import { useRef, useState } from "react";
import { auth, db } from "../firebase/firebase.js";
import { doc, setDoc, getDoc } from "firebase/firestore";
import { debounce } from "lodash"; // เพิ่ม lodash สำหรับ debounce

const provider = new GoogleAuthProvider();
const facebookProvider = new FacebookAuthProvider();

const Login = () => {
  const emailRef = useRef();
  const passwordRef = useRef();
  const [errorMessage, setErrorMessage] = useState("");
  const [wrongPasswordCount, setWrongPasswordCount] = useState(0);
  const [showForgotLink, setShowForgotLink] = useState(false);

  const handleEmailLogin = async (event) => {
    event.preventDefault();
    setErrorMessage("");
    setShowForgotLink(false);

    const email = emailRef.current.value.trim();
    const password = passwordRef.current.value;

    // ตรวจสอบรูปแบบอีเมล
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setErrorMessage("รูปแบบอีเมลไม่ถูกต้อง");
      return;
    }

    try {
      // ตรวจสอบว่าอีเมลมีอยู่ในระบบหรือไม่
      const signInMethods = await fetchSignInMethodsForEmail(auth, email);
      if (signInMethods.length === 0) {
        setErrorMessage("ไม่พบบัญชีผู้ใช้นี้");
        return;
      }

      // พยายามล็อกอิน
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      console.log("Email login successful:", userCredential.user.uid);
      setWrongPasswordCount(0);
      setShowForgotLink(false);
    } catch (error) {
      console.error("Email login error:", error);

      if (error.code === "auth/wrong-password" || error.code === "auth/invalid-credential") {
        const newCount = wrongPasswordCount + 1;
        setWrongPasswordCount(newCount);
        if (newCount >= 3) {
          setErrorMessage("รหัสผ่านไม่ถูกต้องเกิน 3 ครั้ง");
          setShowForgotLink(true);
        } else {
          setErrorMessage("รหัสผ่านไม่ถูกต้อง");
        }
      } else if (error.code === "auth/user-not-found") {
        setErrorMessage("ไม่พบบัญชีผู้ใช้นี้");
      } else if (error.code === "auth/too-many-requests") {
        setErrorMessage("ลองผิดบ่อยเกินไป กรุณารอสักครู่แล้วลองใหม่");
        setShowForgotLink(true);
      } else {
        setErrorMessage("เกิดข้อผิดพลาด: " + error.message);
      }
    }
  };

  // เพิ่ม debounce เพื่อป้องกันการเรียกซ้ำเร็วเกินไป
  const debouncedHandleEmailLogin = debounce(handleEmailLogin, 1000);

  const handleGoogleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      const userDocRef = doc(db, "users", user.uid);
      const userDoc = await getDoc(userDocRef);

      if (!userDoc.exists()) {
        await setDoc(userDocRef, {
          username: user.displayName || "No Name",
          email: user.email,
          telephone: "",
        });
      }

      console.log("Google login successful:", user.uid);
    } catch (error) {
      console.error("Google login error:", error);
      setErrorMessage("เกิดข้อผิดพลาดในการล็อกอินด้วย Google: " + error.message);
    }
  };

  const handleFacebookLogin = async () => {
    try {
      const result = await signInWithPopup(auth, facebookProvider);
      const user = result.user;

      const userDocRef = doc(db, "users", user.uid);
      const userDoc = await getDoc(userDocRef);

      if (!userDoc.exists()) {
        await setDoc(userDocRef, {
          username: user.displayName || "No Name",
          email: user.email,
          telephone: "",
        });
      }

      console.log("Facebook login successful:", user.uid);
    } catch (error) {
      console.error("Facebook login error:", error);
      setErrorMessage("เกิดข้อผิดพลาดในการล็อกอินด้วย Facebook: " + error.message);
    }
  };

  return (
    <>
      <h2>เข้าสู่ระบบ</h2>
      <form onSubmit={debouncedHandleEmailLogin}>
        <div>
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" required ref={emailRef} />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input type="password" id="password" required ref={passwordRef} />
        </div>

        {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
        {showForgotLink && (
          <p>
            <a href="/reset-password">ลืมรหัสผ่าน? คลิกที่นี่เพื่อเปลี่ยน</a>
          </p>
        )}

        <button type="submit">Login</button>
      </form>

      <p>
        ยังไม่มีบัญชีผู้ใช้ ? <a href="/register">Sign Up</a>
      </p>
      <h2>or connect with</h2>
      <button onClick={handleGoogleLogin}>Login with Google</button>
      <button onClick={handleFacebookLogin}>Login with Facebook</button>
    </>
  );
};

export default Login;
