import { getAuth, signInWithEmailAndPassword, GoogleAuthProvider, signInWithPopup, FacebookAuthProvider,sendPasswordResetEmail } from "firebase/auth";
import { useRef, useState} from "react";
import { auth, db } from "../firebase/firebase.js"; 
import { doc, setDoc,getDoc } from "firebase/firestore";

const provider = new GoogleAuthProvider();
const facebookProvider = new FacebookAuthProvider();

const Login = () => {
  const emailRef = useRef();
  const passwordRef = useRef();
  const [errorMessage, setErrorMessage] = useState("");
  const [wrongPasswordCount, setWrongPasswordCount] = useState(0);

  const handleEmailLogin = async (event) => {
    event.preventDefault();
    setErrorMessage(""); // เคลียร์ข้อความก่อน
    const email = emailRef.current.value;
    const password = passwordRef.current.value;

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      console.log("Email login successful:", userCredential.user.uid);
      setWrongPasswordCount(0); // รีเซ็ต counter เมื่อ login สำเร็จ
    } catch (error) {
      console.error("Email login error:", error);

      if (error.code === "auth/wrong-password") {
        const newCount = wrongPasswordCount + 1; // เพิ่ม counter
        setWrongPasswordCount(newCount); // อัพเดต state
        if (newCount >= 3) {
          setErrorMessage("ลืมรหัสผ่านหรือไม่ ต้องการเปลี่ยนรหัสผ่านไหม?"); // แสดงเมื่อผิดเกิน 3 ครั้ง
        } else {
          setErrorMessage("รหัสผ่านไม่ถูกต้อง"); // แสดงเมื่อผิดน้อยกว่า 3 ครั้ง
        }
      } else if (error.code === "auth/user-not-found") {
        setErrorMessage("ชื่อผู้ใช้งานไม่ถูกต้อง");
      } else {
        setErrorMessage(""); // ไม่แสดงข้อความ error อื่นๆ ตามคำขอ
      }
    }
  };
    // ฟังก์ชัน Login ด้วย Google
    const handleGoogleLogin = async () => {
        try {
          const result = await signInWithPopup(auth, provider);
          const user = result.user;
    
          // เช็คว่า user นี้มีข้อมูลใน Firestore หรือยัง
          const userDocRef = doc(db, "users", user.uid);
          const userDoc = await userDocRef.get?.();
    
          if (!userDoc || !userDoc.exists()) {
            // ถ้ายังไม่มีข้อมูลให้เพิ่ม user ลง Firestore
            await setDoc(userDocRef, {
              username: user.displayName || "No Name",
              email: user.email,
              telephone: "",
            });
          }
    
          console.log("Google login successful, user:", user.uid);
        } catch (error) {
          console.error("Google login error:", error);
        }
      };

      // Login ด้วย Facebook
  const handleFacebookLogin = async () => {
    try {
      const result = await signInWithPopup(auth, facebookProvider);
      const user = result.user;

      const userDocRef = doc(db, "users", user.uid);
      const userDoc = await getDoc(userDocRef);

      if (!userDoc.exists()) {
        await setDoc(userDocRef, {
          username: user.displayName || "No Name",
          email: user.email,
          telephone: "",
        });
      }

      console.log("Facebook login successful:", user.uid);
    } catch (error) {
      console.error("Facebook login error:", error);
    }
  };

    return (
    <>
      <h2>เข้าสู่ระบบ</h2>
      <form onSubmit={handleEmailLogin}>
        <div>
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" required ref={emailRef} />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input type="password" id="password" required ref={passwordRef} />
        </div>

        {errorMessage && (
          <p style={{ color: "red" }}>
            {errorMessage}{" "}
            {errorMessage.includes("ลืมรหัสผ่าน") && (
              <a href="/reset-password">คลิกที่นี่</a>
            )}
          </p>
        )}
        <button type="submit">Login</button>
      </form>

      <p>
        ยังไม่มีบัญชีผู้ใช้ ? <a href="/register">Sign Up</a>
      </p>
    <h2>or connect with</h2>
      <button onClick={handleGoogleLogin}>Login with Google</button>
      <button onClick={handleFacebookLogin}>Login with Facebook</button>
    </>
  );
}

export default Login;
