import { getAuth, signInWithEmailAndPassword, GoogleAuthProvider, signInWithPopup, FacebookAuthProvider } from "firebase/auth";
import { useRef } from "react";
import { auth, db } from "../firebase/firebase.js"; 
import { doc, setDoc,getDoc } from "firebase/firestore";

const provider = new GoogleAuthProvider();
const facebookProvider = new FacebookAuthProvider();

const Login = () => {
  const emailRef = useRef();
  const passwordRef = useRef();

  // Login ด้วย Email / Password
  const handleEmailLogin = async (event) => {
    event.preventDefault();
    const email = emailRef.current.value;
    const password = passwordRef.current.value;

     if (password.length < 6) {
    alert("Password ต้องมีอย่างน้อย 6 ตัวอักษร");
    return; // หยุดการทำงานถ้ารหัสผ่านสั้นเกินไป
  }

    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      console.log("Email login successful:", userCredential.user.uid);
    } catch (error) {
      console.error("Email login error:", error);
    }
  };

    // ฟังก์ชัน Login ด้วย Google
    const handleGoogleLogin = async () => {
        try {
          const result = await signInWithPopup(auth, provider);
          const user = result.user;
    
          // เช็คว่า user นี้มีข้อมูลใน Firestore หรือยัง
          const userDocRef = doc(db, "users", user.uid);
          const userDoc = await userDocRef.get?.();
    
          if (!userDoc || !userDoc.exists()) {
            // ถ้ายังไม่มีข้อมูลให้เพิ่ม user ลง Firestore
            await setDoc(userDocRef, {
              username: user.displayName || "No Name",
              email: user.email,
              telephone: "",
            });
          }
    
          console.log("Google login successful, user:", user.uid);
        } catch (error) {
          console.error("Google login error:", error);
        }
      };

      // Login ด้วย Facebook
  const handleFacebookLogin = async () => {
    try {
      const result = await signInWithPopup(auth, facebookProvider);
      const user = result.user;

      const userDocRef = doc(db, "users", user.uid);
      const userDoc = await getDoc(userDocRef);

      if (!userDoc.exists()) {
        await setDoc(userDocRef, {
          username: user.displayName || "No Name",
          email: user.email,
          telephone: "",
        });
      }

      console.log("Facebook login successful:", user.uid);
    } catch (error) {
      console.error("Facebook login error:", error);
    }
  };

    return (
    <>
    <h2>เข้าสู่ระบบ</h2>
      <form onSubmit={handleEmailLogin}>
        <div>
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" required ref={emailRef} />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input type="password" id="password" required ref={passwordRef} />
        </div>
        <button type="submit">Login</button>
      </form>

      <p>
        ยังไม่มีบัญชีผู้ใช้ ? <a href="/register">Sign Up</a>
      </p>
    <h2>or connect with</h2>
      <button onClick={handleGoogleLogin}>Login with Google</button>
      <button onClick={handleFacebookLogin}>Login with Facebook</button>
    </>
  );
}

export default Login;
